import "core-js/modules/es.array.push.js";
import request from "@/utils/request";
import { setToken } from "@/utils/auth";
import Cookies from "js-cookie";
export default {
  name: "Login",
  data() {
    return {
      admin: {},
      loginAdmin: {},
      imgList: [require('../../assets/img1.jpg'), require('../../assets/img2.jpg'), require('../../assets/img3.jpg'), require('../../assets/img4.jpg')],
      rules: {
        username: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }, {
          min: 3,
          max: 10,
          message: '长度在3-10个字符',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }, {
          min: 3,
          max: 10,
          message: '长度在3-10个字符',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    login() {
      this.$refs['loginForm'].validate(valid => {
        if (valid) {
          request.post('/login', this.admin).then(res => {
            // console.log("test")
            if (res.code === 200) {
              // 弹出滑块组件并将后端返回的数据赋值给loginAdmin
              this.loginAdmin = res.data;
            } else {
              this.$notify.error(res.msg);
            }
          });
        }
      });
    },
    // 滑块验证成功后
    onSuccess() {
      console.log('onSuccess');
      let json;
      if (this.loginAdmin !== null) {
        localStorage.setItem("token", this.loginAdmin.token);
        setToken(this.loginAdmin.token);
        json = {
          "id": Number(this.loginAdmin.id),
          "username": this.loginAdmin.username,
          "phone": this.loginAdmin.phone,
          "email": this.loginAdmin.email
        };
        Cookies.set("admin", JSON.stringify(json));
      }
      console.log("login");
      this.$notify.success("登录成功");
      this.$router.push('/');
    },
    onFail() {
      console.log('onFail');
    },
    onRefresh() {
      console.log('onRefresh');
    },
    esc() {
      this.loginAdmin = {};
    }
  }
};
