import "core-js/modules/es.array.push.js";
import request from "@/utils/request";
import Cookies from "js-cookie";
import { removeToken } from "@/utils/auth";
import { checkAdmin } from "@/utils/check";
export default {
  name: 'AdminList',
  data() {
    return {
      admin: Cookies.get('admin') ? JSON.parse(Cookies.get('admin')) : {},
      tableData: [],
      total: 0,
      form: {},
      dialogFormVisible: false,
      isAdmin: true,
      params: {
        pageNum: 1,
        pageSize: 10,
        username: '',
        phone: '',
        email: ''
      },
      rules: {
        newPassword: [{
          required: true,
          message: '请输入新密码',
          trigger: 'blur'
        }, {
          min: 3,
          max: 10,
          message: '长度在3-10个字符',
          trigger: 'blur'
        }]
      }
    };
  },
  created() {
    checkAdmin();
    this.load();
  },
  methods: {
    load() {
      request.get('/admin/page', {
        params: this.params
      }).then(res => {
        if (res.code === 200) {
          console.log(res);
          this.isSuperAdmin();
          // console.log('this.params: ', this.params)
          // console.log('total: ', res.data.total)
          // console.log('pageNum: ', res.data.current)
          // console.log('pageSize: ', res.data.size)
          this.total = Number(res.data.total);
          this.tableData = res.data.records;
        }
      });
    },
    reset() {
      this.params = {
        pageNum: 1,
        pageSize: 10,
        username: '',
        phone: '',
        email: ''
      };
      this.load();
    },
    isSuperAdmin() {
      const id = JSON.parse(Cookies.get('admin')).id;
      console.log(id);
      if (id === 1) {
        this.isAdmin = true;
      } else {
        this.isAdmin = false;
      }
    },
    handleCurrentChange(pageNum) {
      // 点击触发分页按钮
      console.log(pageNum);
      this.params.pageNum = pageNum;
      this.load();
    },
    del(id) {
      request.delete('/admin/delete/' + id).then(res => {
        console.log('delRes: ', res);
        if (this.admin.id === id) {
          // 当前删除的用户id等于当前登陆的管理员id
          this.$notify.warning('不能删除当前正在登录的账号');
          return;
        }
        if (res.code === 200) {
          this.$notify.success('删除成功');
          this.load();
        } else {
          this.$notify.error(res.msg);
        }
      });
    },
    changePwd(row) {
      this.form = row;
      this.dialogFormVisible = true;
    },
    savePwd() {
      this.$refs['formRef'].validate(valid => {
        if (valid) {
          request.put('/admin/changePassword', this.form).then(res => {
            if (res.code === 200) {
              this.$notify.success('修改密码成功');
              if (this.form.id === this.admin.id) {
                // 当前修改的用户id等于当前登陆的管理员id，修改成功之后需要重新登陆
                Cookies.remove('Admin-Token');
                Cookies.remove('admin');
                this.$notify.success('当前登录用户密码被修改，请重新登录');
                this.$router.push('/login');
              }
              // console.log(res)
              this.dialogFormVisible = false;
              this.load();
            } else {
              this.$notify.error('修改密码失败');
            }
          });
        }
      });
    },
    changeStatus(row) {
      if (row.id === 1) {
        row.status = true;
        this.$notify.warning("超级管理员状态不可被修改");
        return;
      }
      if (this.admin.id === row.id) {
        row.status = true;
        this.$notify.warning("当前已登录的用户状态不可修改");
        return;
      }
      request.put('admin/update', row).then(res => {
        console.log(res);
        if (res.code === 200) {
          this.$notify.success('状态更新成功');
          this.load();
        } else {
          this.$notify.error(res.msg);
        }
      });
    }
  }
};
