import request from "@/utils/request";
import Cookies from "js-cookie";
import { checkAdmin } from "@/utils/check";
export default {
  name: 'BorrowList',
  data() {
    return {
      admin: Cookies.get('admin') ? JSON.parse(Cookies.get('admin')) : {},
      tableData: [],
      data: {},
      total: 0,
      statusList: [{
        value: '已归还',
        label: '已归还'
      }, {
        value: '未归还',
        label: '未归还'
      }],
      params: {
        pageNum: 1,
        pageSize: 10,
        bookName: '',
        bookNumber: '',
        userName: '',
        userUsername: ''
      }
    };
  },
  created() {
    checkAdmin();
    this.load();
  },
  methods: {
    load() {
      request.get('/borrow/page', {
        params: this.params
      }).then(res => {
        if (res.code === 200) {
          console.log(res);
          this.total = Number(res.data.total);
          this.tableData = res.data.records;
          this.data = res.data;
          console.log('this.data: ', this.data);
        }
      });
    },
    reset() {
      this.params = {
        pageNum: 1,
        pageSize: 10,
        bookName: '',
        bookNumber: '',
        userName: '',
        userUsername: ''
      };
      this.load();
    },
    handleCurrentChange(pageNum) {
      // 点击触发分页按钮
      console.log(pageNum);
      this.params.pageNum = pageNum;
      this.load();
    },
    del(id) {
      request.delete('/borrow/delete/' + id).then(res => {
        if (res.code === 200) {
          this.$notify.success('删除成功');
          this.load();
        } else {
          this.$notify.error(res.msg);
        }
      });
    },
    returnBook(id) {
      request.post('/borrow/returnBook/' + id).then(res => {
        console.log(res);
        if (res.code === 200) {
          this.$notify.success('还书成功');
          this.load();
        } else {
          this.$notify.error(res.msg);
        }
      });
    }
  }
};
